import React from 'react';

export default class Form extends React.Component {
  constructor(props) {
    super(props);

    this.state = { origin: '',
  destination: '',
  mode: ''
  };
  }


  handleInputChange = e => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };


  handleSubmit = e => {
    e.preventDefault();

    const { origin, destination, mode } = this.state;

    const journey = {
      origin, destination, mode
    };

    alert(journey.mode)
  }


  render() {
      return (
        <form onSubmit={this.handleSubmit}>
          <h1>Carbon Calculator</h1>
          <p>From</p>
          <input
            type="text"
            name="origin"
            onChange={this.handleInputChange}
          />
          <p>To</p>
          <input
            type="text"
            name = 'destination'
            onChange={this.handleInputChange}
          />
    <select name = 'mode' onChange={this.handleInputChange} value={this.state.value}> >

        <option value="Car" onChange={this.handleInputChange}>Car</option>

      </select>
          <button type="submit">
                Calculate
              </button>
        </form>
      );
    }
  }
