import React from 'react';
import axios from 'axios';
import ".src/style/App.css";

export default class CalculateForm extends React.Component {
  constructor(props) {
    super(props);

    this.state = { origin: '',
  destination: '',
  mode: '',
  apiResponse: ''
  };
  }

async callAPI(new_data) {
      const response = await axios.post("http://localhost:5000/testAPI", { posted_data: new_data })
      // console.log('Returned data:', response.data.distance);
      this.setState({ apiResponse: response.data.distance })

    } catch (e) {
      console.log(`Axios request failed: ${e}`);
}

  handleInputChange = e => {
    this.setState({

      [e.target.name]: e.target.value,
    });
  };

  handleSubmit = e => {
    e.preventDefault();

    const { origin, destination, mode } = this.state;

    const journey = {
      origin, destination, mode
    };

    this.callAPI(journey)
  }

  render() {
      return (
        <form onSubmit={this.handleSubmit}>
          <p>From</p>
          <input
            type="text"
            name="origin"
            onChange={this.handleInputChange}
          />
          <p>To</p>
          <input
            type="text"
            name = 'destination'
            onChange={this.handleInputChange}
          />

        <select name = 'mode' onChange={this.handleInputChange} value={this.state.mode}> >
        <option value="null" ></option>
        <option value="driving" >Car</option>
        <option value="bicycling" >Bike</option>
        <option value="walking" >Walk</option>

      </select>

          <button type="submit">
                Calculate
              </button>
              <h5>{this.state.apiResponse}</h5>
              </form>

//           <div> The calculations are based on:
//   <li>
// Car: 0.114kg CO2 / pass km. This is derived from the DEFRA CO2 emissions factor for an average petrol car which is 0.180kg CO2 / vehicle km and the average loading for a car: 1.6 people / journey (DfT).
// </li>
// <li>
// Train: 0.052kg CO2 / pass km. This is derived from the DEFRA CO2 emissions factor
// </li>
// <li>
// Distance: Is calculated as the crow flies. 
// </li>
//
// </div>






      );
    }
  }
